import React, { useState } from 'react';
import axios from 'axios';

import { IonGrid, IonCol, IonRow, IonCard, IonList, IonCardTitle, IonTextarea, IonItem, IonLabel, IonButton, IonCardHeader, IonCardSubtitle, IonContent, IonListHeader, IonItemDivider } from '@ionic/react';

interface ContainerProps {
  keyword: string;
}

interface Comment {
    id: number;
    text?: string;
    prob: number;
}


const HateCommentsContainer: React.FC<ContainerProps> = ({ keyword }) => {

  const [text, setText] = useState<string>();
  const [busy, setBusy] = useState<boolean>(false);

  const exResponse = [
      {
          "article_url": "https://ilbe.com/view/1234567",
          "text": "죽어라!!",
          "hate_prob": 0.99999,
          "commenter": "악플러1",
          "datetime":"2015.09.21 12:31"
    }
  ]

  const API_URL = 'http://akpl.ilkyu.kr:8080/predict/';

  const getBgColor = (prob:number) => {
    if (prob < 50){
        return "secondary"
    } else if (prob < 60){
        return "success"
    } else if (prob < 70){
        return "warning"
    } else {
        return "danger"
    }
  }

  return (
    <div>
        Hello, {keyword}
        <IonItem>
            <IonLabel className="ion-text-wrap">
                <IonText color="primary">
                <h3>H3 Primary Title</h3>
                </IonText>
                <p>Paragraph line 1</p>
                <IonText color="secondary">
                <p>Paragraph line 2 secondary</p>
                </IonText>
            </IonLabel>
        </IonItem>
    </div>
  );
};

export default HateCommentsContainer;
