import React, { useState } from 'react';
import { IonCard, IonCardTitle, IonTextarea, IonItem, IonLabel, IonButton, IonCardHeader, IonCardSubtitle, IonContent } from '@ionic/react';

interface ContainerProps {
  name: string;
}

interface Comment {
    id: number;
    text?: string;
    prob: number;
}


const HatePredictContainer: React.FC<ContainerProps> = ({ name }) => {
  const [text, setText] = useState<string>();
  const [history, setHistory] = useState<Comment[]>([]);

  const predictComment = () => {
    let comment = {
        id: history.length,
        text: "hello",
        prob: 0.66
    }
    setHistory([...history, comment])
    setText("");
  }

  return (
    <div className="container">
      <strong>{name}</strong>
      <IonItem>
        <IonLabel position="floating">Description</IonLabel>
        <IonTextarea 
            value={text} 
            onIonChange={e => setText(e.detail.value!)}
        ></IonTextarea>
      </IonItem>
      <IonButton onClick={predictComment} expand="block">Predict</IonButton>
      <IonContent>
          {history.map((comment, i) => (
            <IonCard>
                <IonCardTitle>
                    {comment.text}
                </IonCardTitle>
                <IonCardSubtitle>
                    {comment.prob}
                </IonCardSubtitle>
            </IonCard>
          ))}
      </IonContent>
    </div>
  );
};

export default HatePredictContainer;
