import React, { useState } from 'react';
import axios from 'axios';

import { IonGrid, IonCol, IonRow, IonCard, IonList, IonCardTitle, IonTextarea, IonItem, IonLabel, IonButton, IonCardHeader, IonCardSubtitle, IonContent, IonListHeader, IonItemDivider, IonText, IonNote } from '@ionic/react';

interface ContainerProps {
  keyword: string;
}

interface Comment {
    id: number;
    text?: string;
    prob: number;
}


const HateCommentsContainer: React.FC<ContainerProps> = ({ keyword }) => {

  const [text, setText] = useState<string>();
  const [busy, setBusy] = useState<boolean>(false);

  const exResponse = [
      {
          "article_url": "https://ilbe.com/view/1234567",
          "text": "죽어라!!",
          "hate_prob": 0.99999,
          "commenter": "악플러1",
          "datetime":"2015.09.21 12:31"
    }
  ]

  const API_URL = 'http://akpl.ilkyu.kr:8080/predict/';

  const getBgColor = (prob:number) => {
    prob *= 100
    if (prob < 50){
        return "secondary"
    } else if (prob < 60){
        return "success"
    } else if (prob < 70){
        return "warning"
    } else {
        return "danger"
    }
  }

  const renderComments = (keyword:string) => {
      return (
        <IonItem>
            <IonLabel className="ion-text-wrap">
                <IonText color={getBgColor(exResponse[0].hate_prob)}>
                <h1>{exResponse[0].text}</h1>
                </IonText>
                <p>작성자: {exResponse[0].commenter} | {exResponse[0].datetime}</p>
                <IonText color="secondary">
                <p><a target="_blank" href={exResponse[0].article_url}>{exResponse[0].article_url}</a></p>
                </IonText>
            </IonLabel>
            <IonText slot="start" color="primary"><h1>{Math.floor(exResponse[0].hate_prob*100)}%</h1></IonText>
        </IonItem>
      )
  }

  return (
    <div>
        Hello, {keyword}
        {renderComments(keyword)}
    </div>
  );
};

export default HateCommentsContainer;
