import React, { useState } from 'react';
import { IonCard, IonCardTitle, IonTextarea, IonItem, IonLabel, IonButton, IonCardHeader, IonCardSubtitle, IonContent } from '@ionic/react';

interface ContainerProps {
  name: string;
}

interface Comment {
    text?: string;
    prob: number;
}

const HatePredictContainer: React.FC<ContainerProps> = ({ name }) => {
  const [text, setText] = useState<string>();
  const history: (Comment)[];

  const renderHistory = () => {
      return <p>adad</p>
  }

  const predictComment = () => {
    history.push({
        text: text,
        prob: 0.5555
    });
    setText("");
    console.log(history)
  }

  return (
    <div className="container">
      <strong>{name}</strong>
      <IonItem>
        <IonLabel position="floating">Description</IonLabel>
        <IonTextarea 
            value={text} 
            onIonChange={e => setText(e.detail.value!)}
        ></IonTextarea>
      </IonItem>
      <IonButton onClick={predictComment} expand="block">Predict</IonButton>
      <IonContent>
        {renderHistory()}
      </IonContent>
    </div>
  );
};

export default HatePredictContainer;
