import React, { useState } from 'react';
import axios from 'axios';

import { IonCard, IonList, IonCardTitle, IonTextarea, IonItem, IonLabel, IonButton, IonCardHeader, IonCardSubtitle, IonContent } from '@ionic/react';

interface ContainerProps {
  name: string;
}

interface Comment {
    id: number;
    text?: string;
    prob: number;
}


const HatePredictContainer: React.FC<ContainerProps> = ({ name }) => {
  const [text, setText] = useState<string>();
  const [history, setHistory] = useState<Comment[]>([]);
  const [busy, setBusy] = useState<boolean>(false);

  const API_URL = 'http://akpl.ilkyu.kr:8080/predict/';

  const getBgColor = (prob:number) => {
    if (prob < 0.5){
        return "primary"
    } else if (prob < 0.6){
        return "success"
    } else if (prob < 0.7){
        return "warning"
    } else {
        return "danger"
    }
  }

  const predictComment = () => {
    setBusy(true)
    axios.get(API_URL + text).then(resp => resp.data)
        .then((data) => {
            let prob = data.prob;
            let comment = {
                id: history.length,
                text: text,
                prob: prob
            }    

            setHistory([...history, comment])
            setText("");
            console.log(history)
            setBusy(false)
        })
  }

  return (
    <div className="container">
      <strong>{name}</strong>
      <IonItem>
        <IonLabel position="floating">문장을 입력하세요</IonLabel>
        <IonTextarea 
            value={text} 
            placeholder="싹쓰리 너무 좋아요~~"
            onIonChange={e => setText(e.detail.value!)}
        ></IonTextarea>
      </IonItem>
      <IonButton onClick={predictComment} disabled={busy} expand="block">혐오 검사</IonButton>
      <IonList>
          {history.map((comment, i) => (
            <IonCard key={i} color={getBgColor(comment.prob)} >
                <IonCardTitle>
                    {comment.text}
                </IonCardTitle>
                <IonCardSubtitle>
                    {comment.prob}
                </IonCardSubtitle>
            </IonCard>
          ))}
      </IonList>
    </div>
  );
};

export default HatePredictContainer;
