import React, { useState } from 'react';
import { IonCard, IonCardTitle, IonTextarea, IonItem, IonLabel, IonButton, IonCardHeader, IonCardSubtitle } from '@ionic/react';

interface ContainerProps {
  name: string;
}

const HatePredictContainer: React.FC<ContainerProps> = ({ name }) => {
  const [text, setText] = useState<string>();
  const history = [];

  const predictComment = () => {
    history.push(text);
    setText()
  }
  return (
    <div className="container">
      <strong>{name}</strong>
      <IonItem>
        <IonLabel position="floating">Description</IonLabel>
        <IonTextarea 
            value={text} 
            onIonChange={e => setText(e.detail.value!)}
        ></IonTextarea>
      </IonItem>
      <IonButton onClick={predictComment} expand="block">Predict</IonButton>
      <IonCard>
          <IonCardHeader>
              <IonCardTitle>
                  ss
              </IonCardTitle>
              <IonCardSubtitle>
                  {text}
              </IonCardSubtitle>
          </IonCardHeader>
      </IonCard>
    </div>
  );
};

export default HatePredictContainer;
