import React, { useState } from 'react';
import axios from 'axios';

import { IonGrid, IonCol, IonRow, IonCard, IonList, IonCardTitle, IonTextarea, IonItem, IonLabel, IonButton, IonCardHeader, IonCardSubtitle, IonContent, IonListHeader, IonItemDivider, IonText, IonNote, IonSearchbar } from '@ionic/react';

interface ContainerProps {

}

interface Comment {
    article_title: string;
    article_url: string;
    text: string;
    hate_prob: number;
    commenter: string;
    datetime: string;
}


const HateCommentsContainer: React.FC<ContainerProps> = ({  }) => {

  const [text, setText] = useState<string>();
  const [busy, setBusy] = useState<boolean>(false);
  const [comments, setComments] = useState<Comment[]>([]);
  const [keyword, setKeyword] = useState<string>("이효리");

  const exResponse = [
      {
          "article_title": "게시글 제목12345",
          "article_url": "https://ilbe.com/view/1234567",
          "text": "죽어라!!",
          "hate_prob": 0.99999,
          "commenter": "악플러1",
          "datetime":"2015.09.21 12:31"
    }
  ]

  const API_URL = 'http://akpl.ilkyu.kr:8080/find/';

  const getBgColor = (prob:number) => {
    prob *= 100
    if (prob < 50){
        return "secondary"
    } else if (prob < 60){
        return "success"
    } else if (prob < 70){
        return "warning"
    } else {
        return "danger"
    }
  }

  const updateComments = (keyword:string) => {
    setBusy(true);
    axios.get(API_URL + keyword).then(resp => resp.data)
        .then((data) => {
            setComments(data)
        })
        setBusy(false)

  }

  return (
    <div>
        <IonItemDivider>아래에 검색할 키워드를 입력하세요</IonItemDivider>
        <IonSearchbar placeholder="이효리" onIonChange={e => updateComments(e.detail.value!)}></IonSearchbar>

        {comments.map((comment, i) => (
            <IonItem>
                <IonLabel className="ion-text-wrap">
                    <IonText color={getBgColor(comment.hate_prob)}>
                    <h1>{comment.text}</h1>
                    </IonText>
                    <p>{comment.article_title} (<a target="_blank" href={comment.article_url}>{comment.article_url}</a>)</p>
                    <p>작성자: {comment.commenter} | {comment.datetime}</p>
                </IonLabel>
                <IonText slot="start" color={getBgColor(comment.hate_prob)}><h1>{Math.floor(comment.hate_prob*100)}%</h1></IonText>
            </IonItem>
        ))}
    </div>
  );
};

export default HateCommentsContainer;
