import React, { useState } from 'react';
import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonTextarea, IonItem, IonLabel, IonItemDivider, IonList } from '@ionic/react';

interface ContainerProps {
  name: string;
}

const HatePredictContainer: React.FC<ContainerProps> = ({ name }) => {
  const [text, setText] = useState<string>();

  return (
    <div className="container">
      <strong>{name}</strong>
      <IonTextarea rows={6} cols={20} placeholder="Enter any notes here..." 
        value={text} onIonChange={e => setText(e.detail.value!)}></IonTextarea>
    </div>
  );
};

export default HatePredictContainer;
